Object.Field:
import java.sql.Connection;
import java.sql.DatabaseMetaData;
import java.sql.DriverManager;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import java.sql.Statement;
import java.util.ArrayList;
import java.util.logging.Level;
import com.sunopsis.dwg.snpreference.OdiRefLogger;

public class RkmMSSQL {
    OdiRefLogger logger;
    Connection srcCx, repoCx;
    String srcSchema, srcCatalog;
    String srcTabFilter, aliasRemoveChars;
    boolean getSynonyms = false;
    String iMod;

    private DatabaseMetaData metadata; 

    public RkmMSSQL() {};

    public void initialize(String piMod, String[] tabPattern, Connection pSrcConnect, String pSrcCatalog, String pSrcSchema, Connection pRepoConnect, OdiRefLogger logger_in) {    
        logger = logger_in;
        logger.log(Level.INFO, "Reverse Synonyms Started.");
        iMod = piMod;

        srcCatalog = (pSrcCatalog.length() == 0)?null:pSrcCatalog;
        srcSchema  = (pSrcSchema.length() == 0)?null:pSrcSchema;
        srcCx = pSrcConnect;

        repoCx = pRepoConnect;

        srcTabFilter = tabPattern[0];
        getSynonyms = getTableType(tabPattern[1]);
        aliasRemoveChars = tabPattern[2];
        logger.log(Level.INFO, "Reverse Options: Model " + iMod);
        logger.log(Level.INFO, "                 Tabfilter " + srcTabFilter);
        logger.log(Level.INFO, "                 TabType " + getSynonyms);

        try {
            metadata = srcCx.getMetaData();
            logger.log(Level.INFO, "Driver Name:    " + metadata.getDriverName().toString());
            logger.log(Level.INFO, "Driver Version: " + metadata.getDriverVersion());
        } catch (SQLException e) {
            logger.log(Level.INFO, "Exception when initializing source metadata");
            e.printStackTrace();
        }
    }

    public void startReverse(){
        if (getSynonyms)
          getColumnsForSynonyms();
    }

    public void endReverse(){
        /* Connection management is handled by ODI and should not be done inside RKM
                logger.log(Level.INFO, "Disconnecting from Source.");
                if (!srcCx.isClosed()) { srcCx.close() }
                logger.log(Level.INFO, "Disconnecting from Repository2");
                if (!self.repoCx.isClosed()) { repoCx.close() }
         */
        logger.log(Level.INFO, "Reverse End.");                
    }

    protected void getColumnsForSynonyms(){
        String query1 = "select name, base_object_name from " + srcCatalog + ".sys.synonyms where schema_name(schema_id) = '" + srcSchema + "' and type = 'SN' and name like '" + srcTabFilter + "'";
        Statement stmt1 = srcCx.createStatement();
        ResultSet rs1 = stmt1.executeQuery(query1);
        while (rs1.next()) {
          String sname = rs1.getString(1);
          String bname = rs1.getString(2);
          logger.log(Level.INFO, "Synonym name = " + sname + ", Base name = " + bname);
          int fPos = bname.indexOf(".");
          int lPos = bname.lastIndexOf(".");
          if (fPos != lPos) {
            String anotherCatalog = bname.substring(1, fPos-1);
            String anotherSchema = bname.substring(fPos+2, lPos-1);
            String anotherTable = bname.substring(lPos+2, bname.length() - 1);
            if (anotherCatalog.equalsIgnoreCase(srcCatalog))
              continue;

            logger.log(Level.INFO, "catalog = " + anotherCatalog + ", schema = " + anotherSchema + ", table = " + anotherTable);

            /*
            StringBuffer query2 = new StringBuffer();
            query2.append("select S.NAME TABLE_NAME, col.COLUMN_NAME COL_NAME, col.DATA_TYPE DT_DRIVER, col.DOMAIN_NAME COL_DESC, col.ORDINAL_POSITION POS, ISNULL(col.CHARACTER_MAXIMUM_LENGTH,0) + ISNULL(col.NUMERIC_PRECISION,0) LONGC, col.NUMERIC_SCALE SCALEC, CASE substring(col.IS_NULLABLE, 1, 1) When 'N' Then '1' Else '0' End COL_MANDATORY");
            query2.append(" from " + anotherCatalog + ".INFORMATION_SCHEMA.COLUMNS col, " + anotherCatalog + ".INFORMATION_SCHEMA.TABLES tab, " + srcCatalog + ".sys.synonyms S ");
            query2.append(" where col.TABLE_CATALOG = tab.TABLE_CATALOG and col.TABLE_SCHEMA = tab.TABLE_SCHEMA and col.TABLE_NAME = tab.TABLE_NAME and tab.TABLE_TYPE = 'BASE TABLE' and S.TYPE = 'SN' and S.NAME like '" + srcTabFilter + "' and schema_name(S.schema_id) = '" + anotherSchema + "' and S.base_object_name = '['+tab.TABLE_CATALOG+'].['+tab.TABLE_SCHEMA+'].['+tab.TABLE_NAME+']'");
            logger.log(Level.INFO, "query2 = " + query2.toString());

            ResultSet rs2 = stmt1.executeQuery(query2.toString());
            while (rs2.next()) {
              String tableName = rs2.getString("TABLE_NAME");
              String colName = rs2.getString("COL_NAME");
              logger.log(Level.INFO, "Table Name = " + tableName);
              logger.log(Level.INFO, "Column Name = " + colName);
            }
            rs2.close();
            */

            ResultSet rs2 = metadata.getColumns(anotherCatalog, anotherSchema, anotherTable, "%");
            String sqltxt = "insert into SNP_REV_COL (MOD_GUID,TABLE_NAME,COL_NAME,COL_HEADING,COL_DESC_C,DT_DRIVER,POS,LONGC,SCALEC,BYTES,COL_MANDATORY,CHECK_FLOW,CHECK_STAT) values (?,?,?,?,?,?,?,?,?,?,?,?,?)";
            PreparedStatement ps = repoCx.prepareStatement(sqltxt);

            while (rs2.next()) {
              String coltable = sname;
              String colname = rs2.getString("COLUMN_NAME");
              String coltypename = rs2.getString("TYPE_NAME");
              int colsize = rs2.getInt("COLUMN_SIZE");
              int coldec = rs2.getInt("DECIMAL_DIGITS");
              String colremark = rs2.getString("REMARKS");
              String colheading = colname;
              if (colremark != null)
                colheading = colremark.substring(1,35);
              int colpos = rs2.getInt("ORDINAL_POSITION");
              String colnull = rs2.getString("NULLABLE")
              String colisnull = rs2.getString("IS_NULLABLE");
              if (colisnull.equals("NO"))
                colisnull="1";
              else
                colisnull="0";

              logger.log(Level.INFO, String.format("Column: %s, %s, %s, %d, %d, %d, %s, %s", coltable, colname, coltypename, colpos, colsize, coldec, colnull, colisnull));

              ps.clearParameters();
              ps.setString(1, iMod);
              ps.setString(2,coltable);
              ps.setString(3,colname);
              ps.setString(4,colheading);
              ps.setString(5,colremark);
              ps.setString(6,coltypename);
              ps.setInt(7,colpos);
              ps.setInt(8,colsize);
              ps.setInt(9,coldec);
              if (coltypename.startsWith("TIMESTAMP")) {
                ps.setInt(8,coldec);
                ps.setInt(9,0);
              }
              ps.setInt(10,colsize);
              ps.setString(11,colisnull);
              ps.setString(12,'1');
              ps.setString(13,'1');
              ps.executeUpdate();

            }
            rs2.close();
            ps.close();
            repoCx.commit();
          }
        }
        rs1.close();
        stmt1.close();
    }

    protected boolean getTableType(String tabPattern) {
        for (String snpstype : tabPattern.split(";")) {
            if (snpstype.equals("SY")) return true;
        }
        return false;
    }
}

rev     = new RkmMSSQL()
